  const [abilities, setAbilities] = useState();
  const [description, setDescription] = useState();
  const [genus, setGenus] = useState();
  const [color, setColor] = useState();
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    fetch("https://pokeapi.co/api/v2/pokemon/" + pokemonName)
      .then(res => res.json())
      .then(result => {
        if (information)
          fetch(information.species.url)
            .then(res => res.json())
            .then(result => {
              let flavorTextArray = result.flavor_text_entries;
              let n = 0;

              while (flavorTextArray[n].language.name !== "en") {
                n++;
              }

              if (flavorTextArray[n].language.name === "en") {
                setDescription(flavorTextArray[n].flavor_text);
              }

              let generaArray = result.genera;
              n = 0;

              while (generaArray[n].language.name !== "en") {
                n++;
              }

              if (generaArray[n].language.name === "en") {
                setGenus(generaArray[n].genus);
              }

              if (
                result.color.name === "white" ||
                result.color.name == "yellow"
              ) {
                let types = information.types;

                if (types.length >= 1) {
                  setColor(convertColor(types[0].type.name));
                } else {
                  setColor("gray");
                }
              } else {
                setColor(result.color.name);
              }
            });

        document.title = capitalize(pokemonName);
      });

    setIsLoaded(true);
  }, null);

  // const navToPrevious = () => {
  //   if (parseInt(information.id, 10) - 1 > 0) {
  //     /*
  //     window.location.href =
  //       "/pokedex-react/#/pokeinfo/" +
  //       (parseInt(information.id, 10) - 1);
  //     window.location.reload();
  //     */
  //   }
  // }

  // const navToNext = () => {
  //   window.location.href =
  //     "/#/pokeinfo/" + (parseInt(information.id, 10) + 1);
  // }

  const returnToPokedex = () => {
    props.setNameOnPokepage(null);
  }

  const returnAbilityString = () => {
    let abilities = information.abilities;

    console.log(abilities);

    if (abilities.length === 2) {
      return (
        capitalize(abilities[0].ability.name) +
        ", " +
        capitalize(abilities[1].ability.name)
      );
    } else if (abilities.length === 1) {
      return capitalize(abilities[0].ability.name);
    } else {
      return "N/A";
    }
  }

  const returnTypeBoxes = () => {
    let types = information.types;

    if (types.length === 2) {
      var orientation = "to left";
      var colorOne = convertColor(types[0].type.name);
      var colorTwo = convertColor(types[1].type.name);

      return (
        <React.Fragment>
          <span
            style={{
              backgroundColor: colorOne,
              textTransform: "uppercase",
              margin: "5px",
              padding: "2px 10px 2px 10px"
            }}
          >
            {types[0].type.name}
          </span>
          <span
            style={{
              backgroundColor: colorTwo,
              textTransform: "uppercase",
              margin: "5px",
              padding: "2px 10px 2px 10px"
            }}
          >
            {types[1].type.name}
          </span>
        </React.Fragment>
      );
    } else if (types.length === 1) {
      var colorOne = convertColor(types[0].type.name);
      return (
        <React.Fragment>
          <span
            style={{
              backgroundColor: colorOne,
              textTransform: "uppercase",
              margin: "5px",
              padding: "2px 10px 2px 10px"
            }}
          >
            {types[0].type.name}
          </span>
        </React.Fragment>
      );
    } else {
      return <span style={{ backgroundColor: convertColor("???") }}>???</span>;
    }
  }

  // const displayPrevButton = () => {
  //   if (idNum > 1) {
  //     return <NavToPrev currentNum={idNum}></NavToPrev>;
  //   } else {
  //     return null;
  //   }
  // }

  const displayPokemonSprites = () => {
    if (information.sprites.front_shiny != null) {
      return (
        <div className="sprites">
          <div className="two-sprites">
            <div>
              {" "}
              <strong>Normal</strong>
            </div>
            <img src={information.sprites.front_default}></img>
            <img src={information.sprites.back_default}></img>
          </div>

          <div className="two-sprites">
            <div>
              <strong>Shiny</strong>
            </div>
            <img src={information.sprites.front_shiny}></img>
            <img src={information.sprites.back_shiny}></img>
          </div>
        </div>
      );
    } else {
      return (
        <div className="sprites">
          <div className="one-sprite">
            <img src={information.sprites.front_default}></img>
            <img src={information.sprites.back_default}></img>
          </div>
        </div>
      );
    }
  }

  const showInformation = () => {

    if (information) {
      const allInformation = information;

      console.log(allInformation.types);

      let types = allInformation.types;
      const bground = document.getElementById("pokemon-information-background");

      if (types.length === 2) {
        var orientation = "to left";
        var colorOne = convertColor(types[0].type.name);
        var colorTwo = convertColor(types[1].type.name);

        if (bground !== null) {
          bground.style.background =
            "linear-gradient(" +
            orientation +
            ", " +
            colorOne +
            ", " +
            colorTwo +
            ")";
        }
      } else if (types.length === 1) {
        if (bground !== null) {
          bground.style.background = convertColor(
            allInformation.types[0].type.name
          );
        }
      } else {
        if (bground !== null) {
          bground.style.background = convertColor("???");
        }
      }

      if (isLoaded === true/* && error === false*/) {
        return (
          <div className="pokeinfo">
            <div className="pokemon-idnum">
              <span>#{idNum}</span>
            </div>
            <div className="info-box">
              <div className="typeandstat-info">
                <div className="types">{this.returnTypeBoxes()}</div>
              </div>

              {this.displayPokemonSprites()}
            </div>
            <div className="short-description">
              <div className="genus-text">{genus}</div>

              <div className="flavor-text">{description}</div>
            </div>
            <div className="section-box" style={{ background: color }}>
              {" "}
              <span>Profile</span>
            </div>
            <div className="profile-info-box">
              <strong>Height:</strong>

              <span>
                {decimetersToCentimeters(information.height)}cm
              </span>

              <strong>Weight:</strong>

              <span>
                {hectogramsToPounds(information.weight)}kgs
              </span>
            </div>
            <div className="profile-info-box">
              <strong>Abilities:</strong>

              <span>{this.returnAbilityString()}</span>

              <strong>Base Experience:</strong>

              <span>{information.base_experience}</span>
            </div>
            <div className="section-box" style={{ background: color }}>
              {" "}
              <span>Moves</span>
            </div>
            <div className="moves-box">
              <ul>
                {moves.map(m => (
                  <div key={m.move.name}>
                    <MoveCard name={m.move.name} url={m.move.url}></MoveCard>
                  </div>
                ))}
              </ul>
            </div>
            <br></br>
          </div>
        );
      }
    }
  }

  return (
    isLoaded ?
      (<div className="pokeinfo-wrapper">
        <div
          className="pokemon-information-background"
          id="pokemon-information-background"
        >
          <div className="navbar-top">
            <div className="pokemon-name"> {pokemonName}</div>

            {/* {this.displayPrevButton()} */}

            {/* <NavToNext currentNum={idNum}></NavToNext> */}
          </div>
          {showInformation()}

          <div className="return-link">
            <input
              className="return-button"
              type="submit"
              value="Return to Pokédex"
              onClick={returnToPokedex}
            ></input>
          </div>
        </div>
      </div>)
      :
      (<LoadingScreen></LoadingScreen>)
  );
}
export default PokemonInformation;
