import React from 'react'
import './statbar.css'
import { ProgressBar } from 'react-bootstrap'

const StatBar = (props) => {

    const containerStyles = {
        height: 20,
        width: '100%',
        backgroundColor: "#e0e0de",
        borderRadius: 50,
        margin: 50
    }

    const fillerStyles = {
        height: '100%',
        width: `${completed}%`,
        backgroundColor: bgcolor,
        borderRadius: 'inherit',
        textAlign: 'right'
    }

    const labelStyles = {
        padding: 5,
        color: 'white',
        fontWeight: 'bold'
    }

    return (
        <div className="statbar-wrapper" key={props.stat.stat.name}>
            <label>
                {props.stat.stat.name.toUpperCase()}
            </label>

            <div style={containerStyles}>
                <div style={fillerStyles}>
                    <span style={labelStyles}>{`${props.stat.base_stat}%`}</span>
                </div>
            </div>
            {/* <div className="bar-wrapper"> */}
            {/* <ProgressBar className='bar-fill' striped variant="success" now={props.stat.base_stat / 2} /> */}
            {/* </div> */}
            <span></span>
        </div>
    )
}

export default StatBar
