import React, { Component, useEffect, Suspense } from "react";
import "./pokemonstyles.css";
import PokemonCard from "./pokemon-card";
import PokemonInformation from "./pokemoninformation";
import ErrorMessage from "./errormessage";
import LoadingScreen from "./loadingscreen";
import { useState } from "react";
import axios from "axios";
import { BsCircleFill } from 'react-icons/bs'
import { Fade } from "react-reveal";

// import {
//   BrowserRouter,
//   Switch,
//   Route,
//   Link,
//   HashRouter,
//   StaticRouter
// } from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import FilterButton from "./filterbutton";

const insertionSort = array => {
  const length = array.length;

  for (let i = 1; i < length; i++) {
    let key = array[i];
    let j = i - 1;
    while (j >= 0 && array[j].id > key.id) {
      array[j + 1] = array[j];
      j = j - 1;
    }
    array[j + 1] = key;
  }
  return array;
};

const Pokedex = () => {
  const [pokemonList, setPokemonList] = useState([]);
  // var pokemonList = [];
  const [pokemonDataList, setPokemonDataList] = useState([]);
  const [searchedPokemonDataList, setSearchedPokemonDataList] = useState([]);
  const [searchValue, setSearchValue] = useState("");
  const [loading, setLoading] = useState(true);
  const [pokemonClicked, setPokemonClicked] = useState(false);
  const [selectedPokemon, setSelectedPokemon] = useState();
  const [showFilterMenu, setShowFilterMenu] = useState(true);
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [cf, setCf] = useState(false);
  const [sortMethod, setSortMethod] = useState('Lowest - Highest');

  useEffect(() => {
    axios.get("https://pokeapi.co/api/v2/pokemon/?offset=0&limit=151")
      // .then(res => res.json())
      .then((result) => {
        console.log(result.data.results);
        setPokemonList(result.data.results);
        result.data.results.map(pokemon => {
          axios.get(pokemon.url)
            // .then(res => res.json())
            .then((result) => {
              if (result.data.sprites.front_default != null) {
                setPokemonDataList(current => [...current, {
                  name: result.data.name,
                  id: result.data.id,
                  imageUrl: result.data.sprites.front_default,
                  types: [],
                  allInformation: result.data
                }]);
              }

              setSearchedPokemonDataList(...pokemonDataList);
            });
        });
      });
    setLoading(false);
    //setPokemonDataList(insertionSort(pokemonDataList));
    //setPokemonDataList([...pokemonDataList].sort((a, b) => a.id > b.name ? 1 : -1));
  }, [])

  // const setPokemonList = (list) => {
  //   pokemonList = { ...list };
  // }

  const capitalize = s => {
    if (typeof s !== "string") return "";
    return s.charAt(0).toUpperCase() + s.slice(1);
  };

  const setPokemon = (pokeinfo) => {
    console.log(pokeinfo.name);
    setSelectedPokemon(pokeinfo);
    console.log(selectedPokemon);
    setPokemonClicked(true);
    document.title = capitalize(pokeinfo.name);
  }

  const closePokemon = () => {
    setSelectedPokemon(null);
    setPokemonClicked(false);
    document.title = "Pokedex";
  }

  const showPokemonResults = () => {
    //const name = document.getElementById("pokemon-search-bar").value;
    let newSearchList = [];

    if (searchValue === "") {
      newSearchList = { ...pokemonDataList };
    }
    else if (!isNaN(searchValue)) {

    }
    else {
      setSearchedPokemonDataList([...searchedPokemonDataList].filter(e => e.includes(searchValue)));
    }

    setSearchedPokemonDataList(newSearchList);
    //this.setState({ searchedPokemonDataList: newSearchList });
    //setSearchedPokemonDataList(newSearchList);
  }

  const clearFilters = () => {
    if (selectedFilters.length >= 1) {
      setSelectedFilters([]);
      setCf(!cf);
    }
  }

  const filterPokemon = () => {
    let filteredArray = [];

    const filterFunction = (e, f, arr) => {
      if (e.allInformation.types.length >= 2) {
        if ((e.allInformation.types[1].type.name.includes(f.toLowerCase()) ||
          e.allInformation.types[0].type.name.includes(f.toLowerCase())) && !arr.includes(e)) {
          return true;
        }
      }
      else if (e.allInformation.types.length <= 1) {
        if (e.allInformation.types[0].type.name.includes(f.toLowerCase()) && !arr.includes(e)) {
          return true;
        }
      }
      else {
        return false;
      }
    }

    if (selectedFilters.length >= 1) {
      selectedFilters.map(f => {
        filteredArray = [...filteredArray, ...([...pokemonDataList].filter(e => filterFunction(e, f, filteredArray)))];
      });
    }
    else {
      filteredArray = [...pokemonDataList];
    }

    if (sortMethod === 'Lowest - Highest') {
      filteredArray = filteredArray.sort((a, b) => a.id > b.id ? 1 : -1).filter(e => e.name.includes(searchValue) || e.id.toString().includes(searchValue));
    }
    else if (sortMethod === 'Highest - Lowest') {
      filteredArray = filteredArray.sort((a, b) => a.id < b.id ? 1 : -1).filter(e => e.name.includes(searchValue) || e.id.toString().includes(searchValue));
    }
    else if (sortMethod === 'A - Z') {
      filteredArray = filteredArray.sort((a, b) => a.name > b.name ? 1 : -1).filter(e => e.name.includes(searchValue) || e.id.toString().includes(searchValue));
    }
    else if (sortMethod === 'Z - A') {
      filteredArray = filteredArray.sort((a, b) => a.name < b.name ? 1 : -1).filter(e => e.name.includes(searchValue) || e.id.toString().includes(searchValue));
    }

    // previousList = [...dataCopy];
    // dataCopy = dataCopy.filter(e => e.allInformation.types[0].type.name.includes("grass"));

    return filteredArray;
  }

  const inputOnChange = (e) => {
    setSearchValue(e.target.value);
    // console.log(searchValue);
  }

  const toggleFilterMenu = (e) => {
    setShowFilterMenu(!showFilterMenu);
  }

  const appendFilter = (f, remove) => {
    if (!remove) {
      setSelectedFilters((selectedFilters) => [...selectedFilters, f]);
    } else {
      setSelectedFilters((selectedFilters) => selectedFilters.filter(filter => {
        return filter !== f;
      }));
    }
    console.log(selectedFilters);
  }

  const selectSortOrder = (e) => {
    setSortMethod(e.target.value);
    console.log(e.target.value);
  }

  const updateSelectedPokemonInformation = () => { }

  return (
    (<div className="page">
      {/* <HashRouter basename={process.env.PUBLIC_URL} > */}
      <div>
        <div className="pokedex">
          <div className="bar-left">
          </div>

          {pokemonDataList ? (<div className="pokedex-division" id="pokedex-division">
            <div className="topbar-search">

              <Fade left>
                <img className='pokelogo-img' src={require('./images/pokedexlogo.png')} alt='pokelogo'>
                </img>
              </Fade>

              <Fade top>
                <input
                  className="pokemon-search"
                  type="text"
                  onChange={inputOnChange}
                  id="pokemon-search-bar"
                  placeholder="Search for a pokemon..."
                  autoComplete="off"
                  spellCheck="false"
                ></input>
              </Fade>

              <Fade right>
                <div className='pokeball-wrap'>
                  <BsCircleFill className="pokelight-3" border="3px solid black"></BsCircleFill>
                  <BsCircleFill className="pokelight-2"></BsCircleFill>
                  <BsCircleFill className="pokelight-1"></BsCircleFill>

                  <img className='pokeball-img' src={require('./images/pokeball-png.png')} alt='logo'>
                  </img>
                </div>
              </Fade>
            </div>

            <div className="pokemon-section">
              <div className="filter-separate-wrapper">
                <hr className="separationbar" />
                <div>
                  <button className="filter-button" onClick={toggleFilterMenu}>Filters</button>
                  <img className='pokedeximage-img' src={require('./images/pokedeximage.png')} alt='pokelogo'>
                  </img>
                  <select name="sort-order" className="sort-select" onChange={selectSortOrder}>
                    <option>Lowest - Highest</option>
                    <option>Highest - Lowest</option>
                    <option>A - Z</option>
                    <option>Z - A</option>
                  </select>
                </div>
              </div>

              {showFilterMenu ? (<Fade opposite duration={200}><div className='filter-wrapper' key={cf}>
                {/* <div className="region-buttons">
                          <FilterButton label='Kanto Region' addFilter={appendFilter}></FilterButton>
                          <FilterButton label='Johto Region' addFilter={appendFilter}></FilterButton>
                          <FilterButton label='Hoenn Region' addFilter={appendFilter}></FilterButton>
                          <FilterButton label='Sinnoh Region' addFilter={appendFilter}></FilterButton>
                          <FilterButton label='Unova Region' addFilter={appendFilter}></FilterButton>
                          <FilterButton label='Kalos Region' addFilter={appendFilter}></FilterButton>
                          <FilterButton label='Alola Region' addFilter={appendFilter}></FilterButton>
                          <FilterButton label='Galar Region' addFilter={appendFilter}></FilterButton>
                        </div> */}

                <div className='type-buttons'>
                  <FilterButton label='Normal' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Fire' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Water' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Grass' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Electric' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Ice' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Fighting' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Poison' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Ground' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Flying' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Psychic' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Bug' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Rock' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Ghost' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Dark' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Dragon' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Steel' addFilter={appendFilter}></FilterButton>
                  <FilterButton label='Fairy' addFilter={appendFilter}></FilterButton>
                </div>
                <button onClick={clearFilters} className="clearfilter-button">Clear Filters</button>
              </div></Fade>) : <></>}

              <div className="grid">
                {filterPokemon().map(p => (
                  <PokemonCard
                    className="grid-item"
                    imageUrl={p.imageUrl}
                    name={p.name}
                    information={p.allInformation}
                    key={p.id + p.name}
                    setPokeinformation={setPokemon}
                    isEvo={false}
                  ></PokemonCard>
                ))}
              </div>
            </div>

            {/* Pokemon Selected - Show info here */}
            <div className=''>
              <Route
                path="/:name"
                component={PokemonInformation}
              ></Route>
              {pokemonClicked ? <PokemonInformation passFunction={setPokemon} setPokeinformation={selectedPokemon} key={selectedPokemon} closeFunction={closePokemon}>

              </PokemonInformation> : <></>}
            </div>

          </div>) : (<LoadingScreen />)}

          <div className="bar-right">

          </div>
        </div>

        {/* <Route path="/error" component={ErrorMessage}></Route> */}
      </div>
      {/* </HashRouter > */}
    </div >)
  );
}

export default Pokedex;
