import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import axios from 'axios';

const Pokemove = (props) => {
    const [moveInfo, setMoveInfo] = useState();

    useEffect(() => {
        if (props.moveObj) {
            fetch("https://pokeapi.co/api/v2/move/" + props.moveObj.move.name)
                .then(res => res.json())
                .then(result => {
                    if (result)
                        fetch(result.species.url)
                            .then(res => res.json())
                            .then(result => {
                                setMoveInfo(result);
                            });
                });
        }


    }, []);

    return (
        <div>
            {props.moveobj.move.name}
        </div>
    )
}

export default Pokemove
