  const capitalize = s => {
    if (typeof s !== "string") return "";
    return s.charAt(0).toUpperCase() + s.slice(1);
  };

  const setPokemon = (name) => {
    console.log(name);
    setSelectedPokemon(name);
    setPokemonClicked(true);
    document.title = capitalize(name);
  }

  const closePokemon = () => {
    setSelectedPokemon(null);
    setPokemonClicked(false);
    document.title = "Pokedex";
  }

  const showPokemonResults = () => {
    //const name = document.getElementById("pokemon-search-bar").value;
    let newSearchList = [];

    if (searchValue === "") {
      newSearchList = { ...pokemonDataList };
    }
    else if (!isNaN(searchValue)) {

    }
    else {
      setSearchedPokemonDataList([...searchedPokemonDataList].filter(e => e.includes(searchValue)));
    }

    setSearchedPokemonDataList(newSearchList);
    //this.setState({ searchedPokemonDataList: newSearchList });
    //setSearchedPokemonDataList(newSearchList);
  }

  const filterPokemon = () => {
    if (sortMethod === 'Lowest - Highest') {
      return [...pokemonDataList].sort((a, b) => a.id > b.id ? 1 : -1).filter(e => e.name.includes(searchValue) || e.id.toString().includes(searchValue));
    }
    else if (sortMethod === 'Highest - Lowest') {
      return [...pokemonDataList].sort((a, b) => a.id < b.id ? 1 : -1).filter(e => e.name.includes(searchValue) || e.id.toString().includes(searchValue));
    }
    else if (sortMethod === 'A - Z') {
      return [...pokemonDataList].sort((a, b) => a.name > b.name ? 1 : -1).filter(e => e.name.includes(searchValue) || e.id.toString().includes(searchValue));
    }
    else if (sortMethod === 'Z - A') {
      return [...pokemonDataList].sort((a, b) => a.name < b.name ? 1 : -1).filter(e => e.name.includes(searchValue) || e.id.toString().includes(searchValue));
    }

    // Add filter logic here
  }

  const inputOnChange = (e) => {
    setSearchValue(e.target.value);
    console.log(searchValue);
  }

  const toggleFilterMenu = (e) => {
    setShowFilterMenu(!showFilterMenu);
  }

  const toggleFilter = (e) => {
    // setSelectedFilters((selectedFilters) => [...selectedFilters], e.target.textContent);
    // console.log(selectedFilters);
  }

  const selectSortOrder = (e) => {
    setSortMethod(e.target.value);
    console.log(e.target.value);
  }

  const updateSelectedPokemonInformation = () => { }

  return (
    !loading ?
      (
        <div className="page">
          <HashRouter basename={process.env.PUBLIC_URL} >
            <div>
              <Route exact path="/">
                <div className="pokedex">
                  <div className="bar-left">

                  </div>

                  <div className="pokedex-division" id="pokedex-division">
                    <div className="topbar-search">

                      <Fade left>
                        <img className='pokelogo-img' src={require('./images/pokedexlogo.png')} alt='pokelogo'>
                        </img>
                      </Fade>

                      <Fade top>
                        <input
                          className="pokemon-search"
                          type="text"
                          onChange={inputOnChange}
                          id="pokemon-search-bar"
                          placeholder="Search for a pokemon..."
                          autoComplete="off"
                        ></input>
                      </Fade>

                      <Fade right>
                        <div className='pokeball-wrap'>
                          <BsCircleFill className="pokelight-3" border="3px solid black"></BsCircleFill>
                          <BsCircleFill className="pokelight-2"></BsCircleFill>
                          <BsCircleFill className="pokelight-1"></BsCircleFill>

                          <img className='pokeball-img' src={require('./images/pokeball-png.png')} alt='logo'>
                          </img>
                        </div>
                      </Fade>
                    </div>

                    <div className="pokemon-section">
                      <div className="filter-separate-wrapper">
                        <hr className="separationbar" />
                        <div>
                          <button className="filter-button" onClick={toggleFilter} onClick={toggleFilterMenu}>Filters</button>
                          <select name="sort-order" className="sort-select" onChange={selectSortOrder}>
                            <option>Lowest - Highest</option>
                            <option>Highest - Lowest</option>
                            <option>A - Z</option>
                            <option>Z - A</option>
                          </select>
                        </div>
                      </div>

                      {showFilterMenu ? (<Fade opposite duration={200} top><div className='filter-wrapper'>
                        <button className="region-button" onClick={toggleFilter}>
                          Kanto Region
                        </button>
                        <button className="region-button" onClick={toggleFilter}>
                          Johto Region
                        </button>
                        <button className="region-button" onClick={toggleFilter}>
                          Hoenn Region
                        </button>
                        <button className="region-button" onClick={toggleFilter}>
                          Sinnoh Region
                        </button>
                        <button className="region-button" onClick={toggleFilter}>
                          Unova Region
                        </button>
                        <button className="region-button" onClick={toggleFilter}>
                          Kalos Region
                        </button>
                        <button className="region-button" onClick={toggleFilter}>
                          Alola Region
                        </button>
                        <button className="region-button" onClick={toggleFilter}>
                          Galar Region
                        </button>


                        <button className="type-button" onClick={toggleFilter}>
                          Normal
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Fire
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Water
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Grass
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Electric
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Ice
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Fighting
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Poison
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Ground
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Flying
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Psychic
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Bug
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Rock
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Ghost
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Dark
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Dragon
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Steel
                        </button>
                        <button className="type-button" onClick={toggleFilter}>
                          Fairy
                        </button>
                      </div></Fade>) : <></>}

                      <div className="grid">
                        {filterPokemon().map(p => (
                          <PokemonCard
                            className="grid-item"
                            imageUrl={p.imageUrl}
                            name={p.name}
                            information={p.allInformation}
                            key={p.id + p.name}
                            setPokename={setPokemon}
                          ></PokemonCard>
                        ))}
                      </div>
                    </div>

                    {/* Pokemon Selected - Show info here */}
                    <div className=''>
                      {pokemonClicked ? <PokemonInformation pokemonName={selectedPokemon} closeFunction={closePokemon}>

                      </PokemonInformation> : <></>}
                    </div>

                  </div>

                  <div className="bar-right">

                  </div>
                </div>
              </Route>

              <Route
                path="/pokeinfo/:name"
                component={PokemonInformation}
              ></Route>

              <Route path="/error" component={ErrorMessage}></Route>
            </div>
          </HashRouter >

        </div >
      )
      :
      <LoadingScreen></LoadingScreen>
  );
}

