import React, { Component, useEffect } from "react";
import "./pokemonstyles.css";
import PokemonCard from "./pokemon-card";
import PokemonInformation from "./pokemoninformation";
import ErrorMessage from "./errormessage";
import LoadingScreen from "./loadingscreen";
import { useState } from "react";

import {
  BrowserRouter,
  Switch,
  Route,
  Link,
  HashRouter,
  StaticRouter
} from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group";

const insertionSort = array => {
  const length = array.length;

  for (let i = 1; i < length; i++) {
    let key = array[i];
    let j = i - 1;
    while (j >= 0 && array[j].id > key.id) {
      array[j + 1] = array[j];
      j = j - 1;
    }
    array[j + 1] = key;
  }
  return array;
};

const Pokedex = () => {
  const [pokemonList, setPokemonList] = useState([]);
  const [pokemonDataList, setPokemonDataList] = useState([]);
  const [searchedPokemonDataList, setSearchedPokemonDataList] = useState([]);
  const [searchValue, setSearchValue] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadPokemon();
  }, [])

  // constructor(props) {
  //   super(props);
  //   // this.state = {
  //   //   pokemonList: [],
  //   //   pokemonDataList: [],
  //   //   searchedPokemonDataList: [],
  //   // };

  //   // fetch("https://pokeapi.co/api/v2/pokemon/?offset=0&limit=1000")
  //   //   .then(res => res.json())
  //   //   .then(result => {
  //   //     this.setState({ pokemonList: result.results });
  //   //     //console.log(this.state.pokemonList);

  //   //     this.state.pokemonList.map(pokemon => {
  //   //       fetch(pokemon.url)
  //   //         .then(res => res.json())
  //   //         .then(result => {
  //   //           let newList = insertionSort(this.state.pokemonDataList);

  //   //           if (result.sprites.front_default != null) {
  //   //             newList.push({
  //   //               name: result.name,
  //   //               id: result.id,
  //   //               imageUrl: result.sprites.front_default,
  //   //               allInformation: result
  //   //             });
  //   //           }

  //   //           this.setState({
  //   //             pokemonDataList: newList,
  //   //             searchedPokemonDataList: newList
  //   //           });
  //   //           setLoading(true);
  //   //         });
  //   //     });
  //   //   });

  //   console.log(this.state.pokemonDataList);

  //   /*
  //   this.setState({
  //     pokemonList: insertionSort(this.state.pokemonList),
  //     pokemonDataList: insertionSort(this.state.pokemonDataList)
  //   });
  //   */

  //   //console.log(this.pokemonDataList);
  //   this.returnMappedImages = this.returnMappedImages.bind(this);
  //   this.showPokemonResults = this.showPokemonResults.bind(this);
  // }

  const showPokemonResults = () => {
    //const name = document.getElementById("pokemon-search-bar").value;
    let newSearchList = [];

    if (searchValue === "") {
      newSearchList = { ...pokemonDataList };
    }
    else if (!isNaN(searchValue)) {
      pokemonDataList.map(pokemon => {
        if (pokemon.id.toString().includes(searchValue)) {
          newSearchList.push(pokemon);
        }
      });
    }
    else {
      pokemonDataList.map(pokemon => {
        if (pokemon.searchValue.toLowerCase().includes(searchValue.toLowerCase())) {
          newSearchList.push(pokemon);
        }
      });
    }

    //this.setState({ searchedPokemonDataList: newSearchList });
    setSearchedPokemonDataList(newSearchList);
  }

  const returnMappedImages = () => {
    return (
      <div className="grid">
        {searchedPokemonDataList.map(p => (
          <PokemonCard
            className="grid-item"
            imageUrl={p.imageUrl}
            name={p.name}
            information={p.allInformation}
            key={p.name}
          ></PokemonCard>
        ))}
      </div>
    );
  }

  const loadPokemon = () => {
    let sortedList = pokemonDataList;//insertionSort(this.state.pokemonDataList);

    //this.setState({ pokemonDataList: sortedList });
    setPokemonDataList(sortedList);

    fetch("https://pokeapi.co/api/v2/pokemon/?offset=0&limit=1200")
      .then(res => res.json())
      .then(result => {
        // this.setState({ pokemonList: result.results });
        setPokemonList(result.results);

        console.log("load pokemon");
        console.log(result.results);

        result.results.map(pokemon => {
          fetch(pokemon.url)
            .then(res => res.json())
            .then(result => {
              let newList = [];//insertionSort(pokemonDataList);
              if (result.sprites.front_default != null) {
                newList.push({
                  name: result.name,
                  id: result.id,
                  imageUrl: result.sprites.front_default,
                  allInformation: result
                });
              }

              // this.setState({
              //   pokemonDataList: newList,
              //   searchedPokemonDataList: newList
              // });

              setPokemonDataList(newList);
              setSearchedPokemonDataList(newList);
              setLoading(false);
            });
        });
      });
  }

  const updateSelectedPokemonInformation = () => { }

  // return {
  // const {
  //   name,
  //   idnum,
  //   error,
  //   isLoaded,
  //   pokemon,
  //   pokemonHeight,
  //   abilities,
  //   moves,

  //   pokemonList,
  //   pokemonDataList
  // } = this.state;
  return (
    <HashRouter basename={process.env.PUBLIC_URL} >
      <div>
        <Route exact path="/">
          <div className="pokedex">
            <div className="pokedex-division" id="pokedex-division">
              <input
                className="pokemon-search"
                type="text"
                onChange={showPokemonResults}
                id="pokemon-search-bar"
                placeholder="Search for a pokemon..."
                value={searchValue}
              ></input>

              <div className="grid">
                {pokemonDataList.map(p => (
                  <PokemonCard
                    className="grid-item"
                    imageUrl={p.imageUrl}
                    name={p.name}
                    information={p.allInformation}
                    key={p.name}
                  ></PokemonCard>
                ))}
              </div>
            </div>
          </div>
        </Route>

        <Route
          path="/pokeinfo/:name"
          component={PokemonInformation}
        ></Route>

        <Route path="/error" component={ErrorMessage}></Route>
      </div>
    </HashRouter>
  );
}

export default Pokedex;
