import React, { Component } from "react";
import "./damagerelation.css";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

const convertColor = c => {
    if (c === "normal") {
        return "#A8A878";
    } else if (c === "fighting") {
        return "#C03028";
    } else if (c === "flying") {
        return "#A890F0";
    } else if (c === "poison") {
        return "#A040A0";
    } else if (c === "ground") {
        return "#E0C068";
    } else if (c === "rock") {
        return "#B8A038";
    } else if (c === "bug") {
        return "#A8B820";
    } else if (c === "ghost") {
        return "#705898";
    } else if (c === "steel") {
        return "#B8B8D0";
    } else if (c === "fire") {
        return "#F08030";
    } else if (c === "water") {
        return "#6890F0";
    } else if (c === "grass") {
        return "#78C850";
    } else if (c === "electric") {
        return "#F8D030";
    } else if (c === "psychic") {
        return "#F85888";
    } else if (c === "ice") {
        return "#98D8D8";
    } else if (c === "dragon") {
        return "#7038F8";
    } else if (c === "dark") {
        return "#705848";
    } else if (c === "fairy") {
        return "#EE99AC";
    } else if (c === "???") {
        return "#68A090";
    }
};


const DamageRelation = (props) => {

    const [typeArray, setTypeArray] = useState([]);
    const [type1, setType1] = useState();
    const [type2, setType2] = useState();
    let damageRelationInfo = { twoFrom: [], twoTo: [], halfFrom: [], halfTo: [], noneFrom: [], noneTo: [] };

    useEffect(() => {
        setTypeArray(props.type);
        console.log(props.type);
    }, []);

    useEffect(() => {
        if (typeArray.length === 1) {
            axios.get(typeArray[0].type.url)
                .then((result) => {
                    console.log(result.data);
                    setType1(result.data);
                });
        }
        else if (typeArray.length === 2) {
            axios.get(typeArray[0].type.url)
                .then((result) => {
                    setType1(result.data);
                });
            axios.get(typeArray[1].type.url)
                .then((result) => {
                    setType2(result.data);
                });
        }

    }, [typeArray]);

    useEffect(() => {
        // Set damage to and from here depending on whether one or two types
        console.log(type1);
        console.log(type2);


        if (typeArray.length === 1) {
            damageRelationInfo.twoFrom = type1.damage_relations.double_damage_from;
            damageRelationInfo.twoTo = type1.damage_relations.double_damage_to;
            damageRelationInfo.halfFrom = type1.damage_relations.half_damage_from;
            damageRelationInfo.halfTo = type1.damage_relations.half_damage_to;
            damageRelationInfo.noneFrom = type1.damage_relations.no_damage_from;
            damageRelationInfo.noneTo = type1.damage_relations.no_damage_to;
        }
        else if (typeArray.length === 2) {
            // setDamageRelationInfo()

        }

    }, [type1, type2])

    return (
        <div className="damage-relations">
            {typeArray && type1 ?
                <div className="damage-modifier-list">
                    <div className="relation">
                        {type1.name} receives 2x damage FROM:

                        {type1.damage_relations.double_damage_from.map(type => (<div>type.name</div>))};


                    </div>

                    <div className="relation">
                        {type1.name} causes 2x damage TO:
                    </div>

                    <div className="relation">
                        {type1.name} receives 0.5x damage FROM:

                    </div>

                    <div className="relation">
                        {type1.name} causes 0.5x damage TO:

                    </div>

                    <div className="relation">
                        {type1.name} Receives no damage FROM:

                    </div>

                    <div className="relation">
                        {type1.name} causes no damage TO:
                    </div>
                </div>
                : <></>}
        </div >
    )
}

export default DamageRelation;
