//   function handleClick() {
//     let n = props.currentNum - 1;

//     if (n > 0) {
//       history.push("/pokeinfo/" + n);
//       window.location.reload();
//     }
//   }

//   return (
//     <button
//       className="previous-btn"
//       type="submit"
//       onClick={handleClick}
//     ></button>
//   );
// }

// function NavToNext(props) {
//   let history = useHistory();

//   function handleClick() {
//     history.push("/pokeinfo/" + (props.currentNum + 1));
//     window.location.reload();
//   }

//   return (
//     <button className="next-btn" type="submit" onClick={handleClick}></button>
//   );
// }

const PokemonInformation = (props) => {
  // console.log(this.props.match.params);

  // this.state = {
  //   searchedName: this.props.match.params.name,
  //   name: null,
  //   idnum: null,
  //   moves: null,
  //   abilities: null,
  //   description: null,
  //   genus: null,
  //   color: null,
  //   allinformation: null,
  //   isLoaded: false,
  //   error: false
  // };

  const [information, setInformation] = useState();
  const [pokemonName, setPokemonName] = useState(props.pokemonName);
  const [idNum, setIdNum] = useState();
  const [moves, setMoves] = useState();
  const [abilities, setAbilities] = useState();
  const [description, setDescription] = useState();
  const [genus, setGenus] = useState();
  const [color, setColor] = useState();
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    fetch("https://pokeapi.co/api/v2/pokemon/" + pokemonName)
      .then(res => res.json())
      .then(result => {
        if (information)
          console.log(information);
        fetch(information.species.url)
          .then(res => res.json())
          .then(result => {
            let flavorTextArray = result.flavor_text_entries;
            let n = 0;

            while (flavorTextArray[n].language.name !== "en") {
              n++;
            }

            if (flavorTextArray[n].language.name === "en") {
              setDescription(flavorTextArray[n].flavor_text);
            }

            let generaArray = result.genera;
            n = 0;

            while (generaArray[n].language.name !== "en") {
              n++;
            }

            if (generaArray[n].language.name === "en") {
              setGenus(generaArray[n].genus);
            }

            if (
              result.color.name === "white" ||
              result.color.name == "yellow"
            ) {
              let types = information.types;

              if (types.length >= 1) {
                setColor(convertColor(types[0].type.name));
              } else {
                setColor("gray");
              }
            } else {
